package com.baizhi.controller;import com.baizhi.entity.Cmfzlog;import com.baizhi.service.CmfzlogService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.HashMap;import java.util.List;import java.util.Map;/** * Created by dell on 2018/6/5. */@RestController@RequestMapping(value = "/cmfzlog")public class CmfzlogConroller {    @Autowired    private CmfzlogService cmfzlogService;    @RequestMapping("/queryalllog")    Map<String,Object> queryall(Integer page, Integer rows){        Map<String, Object> map=new HashMap<String, Object>();        List<Cmfzlog> list=cmfzlogService.queryByPage((page - 1) * rows, rows);        int count=cmfzlogService.querySize();        map.put("total",count);        map.put("rows",list);        return map;    }    @RequestMapping("/insertlog")    void insertLog(Cmfzlog cmfzlog){        cmfzlogService.insert(cmfzlog);    }}