package com.baizhi.entity;import com.fasterxml.jackson.annotation.JsonFormat;import org.springframework.format.annotation.DateTimeFormat;import java.io.Serializable;import java.util.Date;import java.util.List;/** * Created by dell on 2018/5/30. */public class Album implements Serializable {    private String id;    private String title;    @DateTimeFormat(pattern="yyyy-MM-dd")    @JsonFormat(pattern="yyyy-MM-dd",timezone="GMT+8")    private Date uploadtime;    private String author;    private String boyinyuan;    private String coverimage;    private String newname;    private String jianjie;    @DateTimeFormat(pattern="yyyy-MM-dd")    @JsonFormat(pattern="yyyy-MM-dd",timezone="GMT+8")    private Date publishtime;    private Integer zhangjieshu;    private double score;    private List<Music> children;    @Override    public String toString() {        return "Album{" +                "id='" + id + '\'' +                ", title='" + title + '\'' +                ", uploadtime=" + uploadtime +                ", author='" + author + '\'' +                ", boyinyuan='" + boyinyuan + '\'' +                ", coverimage='" + coverimage + '\'' +                ", newname='" + newname + '\'' +                ", jianjie='" + jianjie + '\'' +                ", publishtime=" + publishtime +                ", zhangjieshu=" + zhangjieshu +                ", score=" + score +                ", children=" + children +                '}';    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getNewname() {        return newname;    }    public void setNewname(String newname) {        this.newname = newname;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public Date getUploadtime() {        return uploadtime;    }    public void setUploadtime(Date uploadtime) {        this.uploadtime = uploadtime;    }    public String getAuthor() {        return author;    }    public void setAuthor(String author) {        this.author = author;    }    public String getBoyinyuan() {        return boyinyuan;    }    public void setBoyinyuan(String boyinyuan) {        this.boyinyuan = boyinyuan;    }    public String getCoverimage() {        return coverimage;    }    public void setCoverimage(String coverimage) {        this.coverimage = coverimage;    }    public String getJianjie() {        return jianjie;    }    public void setJianjie(String jianjie) {        this.jianjie = jianjie;    }    public Date getPublishtime() {        return publishtime;    }    public void setPublishtime(Date publishtime) {        this.publishtime = publishtime;    }    public Integer getZhangjieshu() {        return zhangjieshu;    }    public void setZhangjieshu(Integer zhangjieshu) {        this.zhangjieshu = zhangjieshu;    }    public double getScore() {        return score;    }    public void setScore(double score) {        this.score = score;    }    public List<Music> getChildren() {        return children;    }    public void setChildren(List<Music> children) {        this.children = children;    }    public Album() {    }    public Album(String id, String title, Date uploadtime, String author, String boyinyuan, String coverimage,String newname, String jianjie, Date publishtime, Integer zhangjieshu, double score, List<Music> children) {        this.id = id;        this.title = title;        this.uploadtime = uploadtime;        this.author = author;        this.boyinyuan = boyinyuan;        this.coverimage = coverimage;        this.newname = newname;        this.jianjie = jianjie;        this.publishtime = publishtime;        this.zhangjieshu = zhangjieshu;        this.score = score;        this.children = children;    }}