package com.baizhi.entity;import com.fasterxml.jackson.annotation.JsonFormat;import org.springframework.format.annotation.DateTimeFormat;import java.io.Serializable;import java.util.Date;/** * Created by dell on 2018/5/30. */public class Music implements Serializable{   private String id;   private String title;   private Double filesize;   private String path;   @DateTimeFormat(pattern="yyyy-MM-dd")   @JsonFormat(pattern="yyyy-MM-dd",timezone="GMT+8")   private Date uploadtime;   private String url;   private String pic;   private String album_id;    @Override    public String toString() {        return "Music{" +                "id='" + id + '\'' +                ", title='" + title + '\'' +                ", filesize=" + filesize +                ", path='" + path + '\'' +                ", uploadtime=" + uploadtime +                ", url='" + url + '\'' +                ", pic='" + pic + '\'' +                ", album_id='" + album_id + '\'' +                '}';    }    public String getId() {        return id;    }    public String getPic() {        return pic;    }    public void setPic(String pic) {        this.pic = pic;    }    public void setId(String id) {        this.id = id;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public Double getFilesize() {        return filesize;    }    public void setFilesize(Double filesize) {        this.filesize = filesize;    }    public String getPath() {        return path;    }    public void setPath(String path) {        this.path = path;    }    public Date getUploadtime() {        return uploadtime;    }    public void setUploadtime(Date uploadtime) {        this.uploadtime = uploadtime;    }    public String getUrl() {        return url;    }    public void setUrl(String url) {        this.url = url;    }    public String getAlbum_id() {        return album_id;    }    public void setAlbum_id(String album_id) {        this.album_id = album_id;    }    public Music() {    }    public Music(String id, String title, Double filesize, String path, Date uploadtime, String url, String pic,String album_id) {        this.id = id;        this.title = title;        this.filesize = filesize;        this.path = path;        this.uploadtime = uploadtime;        this.url = url;        this.pic = pic;        this.album_id = album_id;    }}