package com.baizhi.entity;import com.fasterxml.jackson.annotation.JsonFormat;import org.springframework.format.annotation.DateTimeFormat;import java.io.Serializable;import java.util.Date;/** * Created by dell on 2018/5/29. */public class Sowingmap implements Serializable{    private String id;    private String title;    private String path;    private String desc1;    private String imagestatus;    private String oldname;    @DateTimeFormat(pattern="yyyy-MM-dd")    @JsonFormat(pattern="yyyy-MM-dd",timezone="GMT+8")    private Date uploadtime;    @Override    public String toString() {        return "Sowingmap{" +                "id='" + id + '\'' +                ", title='" + title + '\'' +                ", path='" + path + '\'' +                ", desc='" + desc1 + '\'' +                ", imagestatus='" + imagestatus + '\'' +                ", oldname='" + oldname + '\'' +                ", uploadtime=" + uploadtime +                '}';    }    public String getId() {        return id;    }    public String getOldname() {        return oldname;    }    public void setOldname(String oldname) {        this.oldname = oldname;    }    public void setId(String id) {        this.id = id;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public String getPath() {        return path;    }    public void setPath(String path) {        this.path = path;    }    public String getDesc1() {        return desc1;    }    public void setDesc1(String desc1) {        this.desc1 = desc1;    }    public String getImagestatus() {        return imagestatus;    }    public void setImagestatus(String imagestatus) {        this.imagestatus = imagestatus;    }    public Date getUploadtime() {        return uploadtime;    }    public void setUploadtime(Date uploadtime) {        this.uploadtime = uploadtime;    }    public Sowingmap() {    }    public Sowingmap(String id, String title, String path, String desc1, String imagestatus,String oldname, Date uploadtime) {        this.id = id;        this.title = title;        this.path = path;        this.desc1 = desc1;        this.imagestatus = imagestatus;        this.oldname = oldname;        this.uploadtime = uploadtime;    }}