package com.baizhi.entity;import com.baizhi.annotation.UserAnnoation;import com.fasterxml.jackson.annotation.JsonFormat;import org.springframework.format.annotation.DateTimeFormat;import java.util.Date;/** * Created by dell on 2018/6/2. */public class User {    @UserAnnoation(name="id")    private String  id;    @UserAnnoation(name="用户名")    private String username;    @UserAnnoation(name="法号")    private String faname;    @UserAnnoation(name="Email")    private String email;    @UserAnnoation(name="电话")    private String tel;    @UserAnnoation(name="密码")    private String password;    @UserAnnoation(name="性别")    private String sex;    @UserAnnoation(name="头像")    private String head_pic;    @UserAnnoation(name="签名")    private String sign;    @UserAnnoation(name="用户状态")    private String userstatus;    @UserAnnoation(name="盐")    private String salt;    @UserAnnoation(name="上师id")    private String teacher_id;    @UserAnnoation(name="省份")    private String province;    @UserAnnoation(name="城市")    private String city;    @DateTimeFormat(pattern="yyyy-MM-dd")    @JsonFormat(pattern="yyyy-MM-dd",timezone="GMT+8")    @UserAnnoation(name="注册时间")    private Date regtime;    @UserAnnoation(name="所在地")    private String ulocation;    public String getUlocation() {        return ulocation;    }    public void setUlocation(String ulocation) {        this.ulocation = ulocation;    }    @Override    public String toString() {        return "User{" +                "id='" + id + '\'' +                ", username='" + username + '\'' +                ", faname='" + faname + '\'' +                ", email='" + email + '\'' +                ", tel='" + tel + '\'' +                ", password='" + password + '\'' +                ", sex='" + sex + '\'' +                ", head_pic='" + head_pic + '\'' +                ", sign='" + sign + '\'' +                ", userstatus='" + userstatus + '\'' +                ", salt='" + salt + '\'' +                ", teacher_id='" + teacher_id + '\'' +                ", province='" + province + '\'' +                ", ulocation='" + ulocation + '\'' +                ", city='" + city + '\'' +                ", regtime=" + regtime +                '}';    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getUsername() {        return username;    }    public void setUsername(String username) {        this.username = username;    }    public String getFaname() {        return faname;    }    public void setFaname(String faname) {        this.faname = faname;    }    public String getEmail() {        return email;    }    public void setEmail(String email) {        this.email = email;    }    public String getTel() {        return tel;    }    public void setTel(String tel) {        this.tel = tel;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }    public String getSex() {        return sex;    }    public void setSex(String sex) {        this.sex = sex;    }    public String getHead_pic() {        return head_pic;    }    public void setHead_pic(String head_pic) {        this.head_pic = head_pic;    }    public String getSign() {        return sign;    }    public void setSign(String sign) {        this.sign = sign;    }    public String getUserstatus() {        return userstatus;    }    public void setUserstatus(String userstatus) {        this.userstatus = userstatus;    }    public String getSalt() {        return salt;    }    public void setSalt(String salt) {        this.salt = salt;    }    public String getTeacher_id() {        return teacher_id;    }    public void setTeacher_id(String teacher_id) {        this.teacher_id = teacher_id;    }    public String getProvince() {        return province;    }    public void setProvince(String province) {        this.province = province;    }    public String getCity() {        return city;    }    public void setCity(String city) {        this.city = city;    }    public Date getRegtime() {        return regtime;    }    public void setRegtime(Date regtime) {        this.regtime = regtime;    }    public User(){}    public User(String id, String username, String faname, String email, String tel, String password, String sex, String head_pic, String sign, String userstatus, String salt, String teacher_id, String province, String city, Date regtime,String ulocation) {        this.id = id;        this.username = username;        this.faname = faname;        this.email = email;        this.tel = tel;        this.password = password;        this.sex = sex;        this.head_pic = head_pic;        this.sign = sign;        this.userstatus = userstatus;        this.salt = salt;        this.teacher_id = teacher_id;        this.province = province;        this.city = city;        this.regtime = regtime;        this.ulocation=ulocation;    }}