package com.baizhi.interceptor;import com.baizhi.entity.Manager;import org.springframework.web.servlet.HandlerInterceptor;import org.springframework.web.servlet.ModelAndView;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import javax.servlet.http.HttpSession;/** * Created by dell on 2018/6/6. */public class LoginInterceptor implements HandlerInterceptor{    public boolean preHandle(HttpServletRequest httpServletRequest, HttpServletResponse httpServletResponse, Object o) throws Exception {        HttpSession session = httpServletRequest.getSession();        Manager manager = (Manager) session.getAttribute("manager");        if(manager==null){            httpServletResponse.sendRedirect(httpServletRequest.getContextPath()+"/login.jsp");            return false;        }else {            return true;        }    }    public void postHandle(HttpServletRequest httpServletRequest, HttpServletResponse httpServletResponse, Object o, ModelAndView modelAndView) throws Exception {    }    public void afterCompletion(HttpServletRequest httpServletRequest, HttpServletResponse httpServletResponse, Object o, Exception e) throws Exception {    }}