package com.baizhi.service;import com.baizhi.annotation.LogAnnotation;import com.baizhi.daos.AlbumDao;import com.baizhi.entity.Album;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.List;/** * Created by dell on 2018/5/30. */@Service@Transactionalpublic class AlbumServiceImpl implements  AlbumService{    @Autowired    private AlbumDao albumDao;    @LogAnnotation(name = "插入专辑")    public void insertAlbum(Album album) {        albumDao.insertAlbum(album);    }    public List<Album> queryall() {        return albumDao.queryall();    }    public Album queryAlbumbyid(String id) {        return albumDao.queryAlbumbyid(id);    }    @LogAnnotation(name = "修改专辑")    public void updateCount(Album album) {        albumDao.updateCount(album);    }}