package com.baizhi.service;import com.baizhi.daos.CmfzlogDao;import com.baizhi.entity.Cmfzlog;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.List;/** * Created by dell on 2018/6/5. */@Service@Transactionalpublic class CmfzlogServiceImpl implements CmfzlogService {    @Autowired    private CmfzlogDao cmfzlogDao;    public int insert(Cmfzlog cmfzlog) {        return cmfzlogDao.insert(cmfzlog);    }    public List<Cmfzlog> selectAll() {        return cmfzlogDao.selectAll();    }    public List<Cmfzlog> queryByPage(Integer start, Integer end) {        return cmfzlogDao.queryByPage(start,end);    }    public Integer querySize() {        return cmfzlogDao.querySize();    }}