package com.baizhi.service;import com.baizhi.annotation.LogAnnotation;import com.baizhi.daos.ManagerDao;import com.baizhi.entity.Manager;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;/** * Created by dell on 2018/5/28. */@Service@Transactionalpublic class ManagerServiceImpl implements ManagerService{    @Autowired    private ManagerDao managerDao;    public Manager login(String username) {        return managerDao.login(username);    }    @LogAnnotation(name = "修改密码")    public void changepassword(Manager manager) {        managerDao.changepassword(manager);    }}