package com.baizhi.service;import com.baizhi.annotation.LogAnnotation;import com.baizhi.daos.MusicDao;import com.baizhi.entity.Music;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;/** * Created by dell on 2018/5/30. */@Service@Transactionalpublic class MusicServiceImpl implements MusicService{    @Autowired    private MusicDao musicDao;    @LogAnnotation(name="插入章节")    public void insertmusic(Music music) {        musicDao.insertmusic(music);    }}