package com.baizhi.service;import com.baizhi.annotation.LogAnnotation;import com.baizhi.daos.SowingmapDao;import com.baizhi.entity.Sowingmap;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.List;/** * Created by dell on 2018/5/29. */@Service@Transactionalpublic class SowingmapServiceImpl implements SowingmapService {    @Autowired    private SowingmapDao sowingmapDao;    @LogAnnotation(name="插入轮播图")    public void insert(Sowingmap sowingmap) {        sowingmapDao.insert(sowingmap);    }    @LogAnnotation(name="删除轮播图")    public void deletes(List<String> list) {        sowingmapDao.deletes(list);    }    @LogAnnotation(name="修改轮播图状态")    public void update(Sowingmap sowingmap) {        sowingmapDao.update(sowingmap);    }    public List<Sowingmap> queryall(Integer start, Integer end) {        return sowingmapDao.queryall(start, end);    }    public Integer querySize() {        return sowingmapDao.querySize();    }}