package com.baizhi.service;import com.baizhi.annotation.LogAnnotation;import com.baizhi.daos.UserDao;import com.baizhi.entity.ProvinceVO;import com.baizhi.entity.User;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.List;/** * Created by dell on 2018/6/2. */@Service@Transactionalpublic class UserSericeImpl implements UserService {    @Autowired    private UserDao userDao;    public List<User> queryAll() {        return userDao.queryAll();    }    public void updateUser(User user) {        userDao.updateUser(user);    }    public Integer queryNumByDate(Integer dates) {        return userDao.queryNumByDate(dates);    }    public List<ProvinceVO> queryNumByProAndSex(String sex) {        return userDao.queryNumByProAndSex(sex);    }    @LogAnnotation(name="导入用户")    public void insertUser(List<User> list) {        userDao.insertUser(list);    }    public List<User> queryByPage(Integer start, Integer end) {        return userDao.queryByPage(start,end);    }    public Integer querySize() {        return userDao.querySize();    }    public User queryByTel(String tel) {        return userDao.queryByTel(tel);    }    public User queryById(String id) {        return userDao.queryById(id);    }    public int registeruser(User user) {        return userDao.registeruser(user);    }    public int updateUsers(User user) {        return userDao.updateUsers(user);    }}